<template>
  <div class="col-md-12">
    <!-- <button @click="updateToken()">update token</button>
    <button @click="productList()">get product list</button> -->
    <div class="col-md-6 m-auto pt-5">
      <p style="font-size: 15spx" class="my-4">How do you want to shop?</p>
      <div class="col-md-12 row">
        <div class="col-md-6" @click="storedisplay = true">
          <div
            class="col-md-12 card p-3"
            style="align-items: center"
            :class="{ active: storedisplay }"
          >
            <!-- <img src="data:image/png;base64," alt="" width="50px" height="auto"> -->
            <img
              src="https://cdn-icons-png.flaticon.com/128/2898/2898372.png"
              width="50px"
              height="auto"
              alt=""
            />
            <p class="mb-0">In store</p>
          </div>
        </div>
        <div class="col-md-6" @click="storedisplay = false">
          <div
            class="col-md-12 card p-3"
            style="align-items: center"
            :class="{ active: !storedisplay }"
          >
            <img
              src="https://cdn-icons-png.flaticon.com/128/126/126122.png"
              width="50px"
              height="auto"
              alt=""
            />
            <p class="mb-0">Online</p>
          </div>
        </div>
      </div>
      <p class="my-4" style="font-size: 15px">
        Visit your preferred store ? <br />
        and experience a better way to shop!
      </p>
      <div
        class="col-md-12 mt-3 border p-3"
        style="background: transparent; border-radius: 5px"
        v-if="storedisplay"
      >
        <p
          style="
            text-align: left;
            font-size: 12px;
            color: red;
            font-weight: 700;
          "
        >
          <i class="fa fa-map-marker" aria-hidden="true"></i> Branch
        </p>
        <select
          class="mb-0 p-2 form-control form-select"
          placeholder="Select a store"
          style="border-radius: 0; font-size: 13px; font-weight: 700"
          v-model="selStore"
        >
          <option value="" disabled>Select a store</option>
          <option
            v-for="(data, i) in storeDatas"
            :key="i"
            class="mb-0 p-3"
            style="font-weight: 400; padding: 10px"
            :value="i"
          >
            {{ data.name }}
          </option>
        </select>
      </div>
      <div class="mt-3">
        <button class="btn btn-primary" @click="updateToken()">Shop Now</button>
      </div>
    </div>
  </div>
</template>

<script>
import axios from "axios";
import setAuthHeaders from "../utils/setAuthHeaders";
export default {
  data() {
    return {
      storeDatas: [],
      storedisplay: true,
      selStore: "",
    };
  },
  created() {
    this.store();
  },
  methods: {
    store() {
      axios
        .get("/auth-service/cws/store/selfCheckoutEnabledStores")
        .then((response) => {
          this.storeDatas = response.data.stores;
        });
    },
    updateToken() {
      console.log("branch datar", this.storeDatas[this.selStore].storeCode);
      const oldd = localStorage.getItem("jwtToken");
      console.log("old token", oldd);
      axios
        .get(
          "/auth-service/cws/store/" +
            String(this.storeDatas[this.selStore].storeCode)
        )
        .then((response) => {
          console.log("justToken", response.headers.authorization);
          const token = response.headers.authorization;
          localStorage.setItem("jwtToken", response.headers.authorization);
          setAuthHeaders(token);
          const neww = localStorage.getItem("jwtToken");
          console.log("new token", neww);
          console.log("response", response.data);
          localStorage.setItem("storeId", response.data.id);
          //this.$store.commit('storeId',response.data.id)
        });
      localStorage.removeItem("cartId");
      this.$router.push("/home");
    },
    // productList() {
    //   axios.post("/product-service/cws/catalog/products/9").then((response) => {
    //     console.log("list", response.data);
    //   });
    // },
  },
};
</script>

